import axios from "axios";
import { headers } from "./Headers";
import { postMapping } from "./AllUrls";

export const addTicket=(ticket)=>{
    return postMapping(`/ticket/add` , ticket , {headers:headers()});
}

export const allTicket=(params)=>{
    return axios.get(`${process.env.REACT_APP_ENV_VARIABE}`+`/ticket/getall`,{params:params,headers:headers(),})
}
