import React, { useEffect, useState } from 'react'
import "../registration/UserRegistration.css"
import { getAllDepartment } from '../../api/Department_API';

function AddTicket() {

    const [department , setDepartment] = useState([]);

    const getDepartment=()=>{
    getAllDepartment().then((resp)=>{
      setDepartment(resp.data);
      console.log(resp.data);
    }).catch((error)=>{
      console.log(error);
    })
    }

    useEffect(()=>{
    getDepartment();
    } , [])
  return (
    <div className='registration'>
      <div className='content'>
      <div className='header'>
        <h1>Add Ticket</h1>
      </div>
      
        <form>
        <div className='user-details'>
        <div className='inner-div'>
            <label for="cars">Ticket Type:</label>

            <select name="ticket-type" >
              <option value="OPEN">OPEN</option>
              <option value="IN-PROGRESS">IN-PROGRESS</option>
              <option value="CLOSED">CLOSED</option>
            </select>
          </div>
          <div className='inner-div'>
            <label>Title</label>
            <input type='text' placeholder='Enter Title' value='' onChange='' />
          </div>

          <div className='inner-div'>
            <label>Discription</label>
            <textarea id="" name="discriptions" placeholder='Enter Discription' rows="4" cols="50"/>
          </div>

          <div className='inner-div'>
            <label for="cars">Department:</label>

            <select name="Role" >
            
              {department.map(dep=>{
                  return <option value={dep.depName}>{dep.depName}</option>
              })}
              <option onClick={()=>{alert("Adding department");}}>Add Department</option>
              {/* <option value="saab">fired</option> */}
            </select>
          </div>
          <div className='inner-div'>
            <label>Status</label>
            <input type='text' value='OPEN' onChange='' disabled/>
          </div>
          
          <div className='input-box'>
                <input type='submit' value='Submit'  />
          </div>

          </div>
        </form>
        </div>
    </div>
  )
}

export default AddTicket
