export const Table= ({ data , columns , onRowClick})=>{
    
    return (
        <table>
            <tr>
                {columns.map((column)=>(
                    <th key={column.key}>{column.title}</th>
                ))}
            </tr>
            {data.map((row)=> (
                <tr key={row.id} onClick={()=> onRowClick(row)}>
                    {columns.map((column)=>(
                        <td key={column.key}>{row[column.key]}</td>
                    ))}
                </tr>
            ))}
        </table>
    )
};
