import React, { useState } from 'react'
import "./LoginPage.css"
import { useNavigate } from 'react-router-dom';
import { login } from '../../api/Employee_API';
function LoginPage() {

    const navigate = useNavigate(); 

    const [data , setData] = useState({
        userName:'',
        password:''
      });
    
      const [valid , setValid] = useState({
        userName:{
            isError:false,
            errorMessage:""
        },
        password:{
            isError:false,
            errorMessage:""
        }
      })
    
      const handleChange=(e)=>{
        setData({
            ...data , 
            [e.target.name] : e.target.value
        })
      }
      
      const handleSubmit=(e)=>{
        e.preventDefault();
        validation();
        login(data).then(res=>{
            alert("saved")
        }).catch(error=>{
            alert("error")
        })
    }

        const validation=()=>{
            const regex = /^[a-z0-9](\.?[a-z0-9]){5,}@g(oogle)?mail\.com$/;
            if(!regex.test(data.userName)){
                setValid({
                    ...valid,
                    isError:true,
                    errorMessage:"Email Should end with @google.com"
                })
            }

            if(data.password.length < 5 && data.password.length > 15){
                setValid({
                    ...valid,
                    isError:true,
                    errorMessage:"Password length must be between 5 to 15"
                })
            } 
        }

  return (
    <div className='outer-div'>
        <div className='image-box'>
            <img src='./logo.png'/>
        </div>
        <div className="content-box">
            <div className='form-box'>
                <h2>
                    Login!
                </h2>
                <form onSubmit={handleSubmit}>
                    <div className='input-box'>
                        <span>UserName</span>
                        <input type = "email" name="userName" value={data.userName} onChange={handleChange} required/>
                        {valid.userName.isError && (<p>{valid.userName.errorMessage}</p>)}
                    </div>
                    <div className='input-box'>
                        <span>Password</span>
                        <input type='password' name="password" value={data.password} onChange={handleChange} required/>
                    </div>
                    <div className='input-box'>
                        <input type='submit' value='Sign in'  />
                    </div>
                </form>
            </div>
        </div>
    </div>
  )
}

export default LoginPage
