import React, { useState } from 'react'
import "../component-css/LoginPage.css"
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
function LoginPage() {

    const navigate = useNavigate(); 

    const [data , setData] = useState({
        userName:"",
        password:""
      });

      const [valid , setValid] = useState({
        email:false,
        password:false
      })
    
      const handleChange=(e)=>{
        setData({...data , [e.target.name]: e.target.value});
        // console.log(data);
      }
    
      const handleSubmit=(e)=>{
        e.preventDefault();
        validation(e);
        axios.post("http://localhost:8080/login",data)
        .then(response=>{
          if(response.data !== 'Invalid User')
            navigate('/welcome')
          else{alert(response.data)}
        })
    }

        const validation=(e)=>{
            e.preventDefault();
            const regex = /^[a-z0-9](\.?[a-z0-9]){5,}@g(oogle)?mail\.com$/;
            if(regex.test(data.userName)){
                setValid({...valid , [valid.email] : true})
                if(data.password.length >= 5 && data.password.length <=15){
                    setValid({...valid , [valid.password] : true})
                }
            }
            else{
                return;
            }
            
        }

  return (
    <div className='outer-div'>
        <div className='image-box'>
            <img src='./logo.png'/>
        </div>
        <div className="content-box">
            <div className='form-box'>
                <h2>
                    Login!
                </h2>
                <form onSubmit={handleSubmit}>
                    <div className='input-box'>
                        <span>UserName</span>
                        <input type = "email" name="userName" value={data.userName} onChange={handleChange} required/>
                    </div>
                    <div className='input-box'>
                        <span>Password</span>
                        <input type='password' name="password" value={data.password} onChange={handleChange} required/>
                    </div>
                    <div className='input-box'>
                        <input type='submit' value='Sign in'  />
                    </div>
                </form>
            </div>
        </div>
    </div>
  )
}

export default LoginPage
