import React, { useState } from 'react'
import Modal from '../Modal/Modal';

function ResetPassword() {

    const [pass , setPass] = useState({
        oldPassword:"",
        newPassword:"",
        confirmPassword:"",
    });

    const [valid , setValid] = useState({
        oldPassword:{
            isError:true,
            errorMessage:"Error message shows here"
        },
        newPassword:{
            isError:false,
            errorMessage:""
        },
        confirmPassword:{
            isError:false,
            errorMessage:""
        },
    })

    const isValid=()=>{
        if(valid.oldPassword === "" ){
            const temp = {...valid};
            temp.oldPassword.isError = true;
            temp.oldPassword.errorMessage = "Password can not be Empty";
            setValid(temp);
        }
        else{
            const temp = {...valid};
            temp.oldPassword.isError = true;
            temp.oldPassword.errorMessage = "Password Range should between 5 to 18";
            setValid(temp);
        }
        if(valid.newPassword === "" ){
            const temp = {...valid};
            temp.newPassword.isError = true;
            temp.newPassword.errorMessage = "Password can not be Empty";
            setValid(temp);
        }
        else{
            const temp = {...valid};
            temp.newPassword.isError = true;
            temp.newPassword.errorMessage = "Password Range should between 5 to 18";
            setValid(temp);
        }
        if(valid.confirmPassword === "" ){
            const temp = {...valid};
            temp.confirmPassword.isError = true;
            temp.confirmPassword.errorMessage = "Password can not be Empty";
            setValid(temp);
        }
        else if(valid.confirmPassword === valid.newPassword){
            const temp = {...valid};
            temp.confirmPassword.isError = true;
            temp.confirmPassword.errorMessage = "New password is not matching with Confirm password";
            setValid(temp);
        }
        else{
            const temp = {...valid};
            temp.confirmPassword.isError = true;
            temp.confirmPassword.errorMessage = "Password Range should between 5 to 18";
            setValid(temp);
        }
    }

    const handleChange=(e)=>{
        setPass({...pass , [e.target.name] : e.target.value});
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        isValid();
        console.log(pass);
    }

    const fields = [
        {
            field:"Input",
            label:"Old Password",
            className:"details",
            type:"password",
            name:"oldPassword",
            value:pass.oldPassword,
            placeholder:"Enter the old password",
            onChange:handleChange,
            isValid:valid.oldPassword.isError,
            errorMessage:valid.oldPassword.errorMessage
        },
        {
            field:"Input",
            label:"New Password",
            className:"details",
            type:"password",
            name:"newPassword",
            value:pass.newPassword,
            placeholder:"Enter the new password",
            onChange:handleChange,
            isValid:valid.newPassword.isError,
            errorMessage:valid.errorMessage
        },
        {
            field:"Input",
            label:"Confirm Password",
            className:"details",
            type:"password",
            name:"confirmPassword",
            value:pass.confirmPassword,
            placeholder:"Retype new  password",
            onChange:handleChange,
            isValid:valid.confirmPassword.isError,
            errorMessage:valid.errorMessage
        },
        {
            field:"Button",
            type:"submit",
            className:"submit-box",
            value:"Submit"
        }
    ]
  return (
    <Modal header={"Change Password"} fields={fields} onsubmit={handleSubmit}/>
  )
}

export default ResetPassword
