import React, { useEffect, useState } from 'react'
import "../components/Department.css"
import axios from 'axios';
import TableHeader from '../components/Table/TableHeader';
import Content from '../components/Table/Content';

function AllTickets() {

    const [ticket , setTicket] = useState([]);

    useEffect(()=>{
        axios.get(`http://localhost:8080/ticket/getall/${1}/${"hr"}`).then(resp=>{
            setTicket(resp.data);
            
        })
    },[])

    const header = [
        "Ticket Id",
        "Ticket Type",
        "Status",
        "Ticket Title",
        "Last Update",
        "Actions"
    ];


  return (
    <div className='department-table'>
    <table>
    <TableHeader header={header}/>

  {ticket.map(d=>{
       return (
        <tr>
          <Content data={d.ticketId}/>
          <Content data={d.ticketType}/>
          <Content data={d.status}/>
          <Content data={d.ticketName}/>
          <Content data={d.lastUpdateDate}/>
          <td><button>View</button></td>
        </tr>
    )})}
  
  
</table>
    </div>
  )
}

export default AllTickets
