import React, { useEffect, useState } from "react";
import Nav from "./Nav";
import AllTickets from "./AllTickets";
import AllDepartment from "./AllDepartment";
import AddTicket from "./AddTicket";
import UserRegistration from "./UserRegistration";
import { Link, Outlet, useNavigate } from "react-router-dom";
import AllEmployees from "./AllEmployees";

function AdminDashboard(props) {
  const ticket = [{name:"All Tickets",to:"/admin"}, {name:"Add Ticket", to:"/admin/addTicket"}];
  const department = [{name:"All Department", to:"/admin/allDepartment"}, {name:"Create Department"}];
  const employee = [{name:"All Employee", to:"/admin/allEmployee"}, {name:"Add Employee", to:"/admin/registration"}];
  const profile = [{name:"Change Password"}, {name:"Logout"}];
  const navigate = useNavigate();
  const elements = [
    {
      name: "Ticket",
      value: ticket,
    },
    {
      name: "Department",
      value: department,
    },
    {
      name: "Employee",
      value: employee,
    },
    {
      name: "Profile",
      value: profile,
    },
  ];

  useEffect(()=>{
    const user = JSON.parse(localStorage.getItem("user"));
    if(user?.role !== "ROLE_ADMIN"){
      navigate("/")
    }
  })

  const [active, setActive] = useState("All Ticket");
  return (
    <>
      {localStorage.getItem("user").role === "ROLE_ADMIN"?
      (<><Nav tag={elements} setActive={setActive} />
       <Outlet></Outlet></>):(navigate("/"))}
    </>
  );
}

export default AdminDashboard;
