import React, { useState } from "react";
import "./Modal.css";

export default function Modal(props) {

    const [depName , setDepName] = useState("");

    const [valid , setValid] = useState({
        isError:false,
        errorMessage:""
    })

    const handleChange=(e)=>{
        if(valid.isError){
            setValid({
                ...valid,
                isError:false
            })
        }
        setDepName(e.target.value);
    }

    const isValid=()=>{
        if(depName === ""){
            setValid({
                ...valid , 
                isError:true,
                errorMessage:"Department name can not be empty"
            })
        }
    }

    const handleSubmit=(e)=>{
        e.preventDefault();
        isValid();

    }

    return (
        <>
        <div className="modal">
          <div className="modal-content">
            <div className="header">
            <span>Add Department</span>
            </div>
                <form onSubmit={handleSubmit}>
                    <div className="details">
                    <label>
                        Department Name :
                    </label>
                    <input type="text" name="depName" value={depName} placeholder="Enter the Departent name" onChange={handleChange}/>
                    {valid.isError && (<p>{valid.errorMessage}</p>)}
                    </div>

                    <div className='submit-box'>
                            <input type='submit' value='Submit'  />
                    </div>
                </form>
            
            {/* <button className="close-modal" onClick={()=>{props.setModal(false)}}>
              X
            </button> */}
          </div>
        </div>
      
    </>
  );
}
