import React, { useState } from "react";
import { Outlet, useNavigate } from "react-router-dom";
import Nav from "./Nav";

function AdminDashboard() {
  const ticket = [{name:"All Tickets",to:"/admin"}, {name:"Add Ticket", to:"/admin/addTicket"}];
  const department = [{name:"All Department", to:"/admin/allDepartment"}, {name:"Create Department"}];
  const employee = [{name:"All Employee", to:"/admin/allEmployee"}, {name:"Add Employee", to:"/admin/registration"}];
  const profile = [{name:"Change Password"}, {name:"Logout"}];
  const navigate = useNavigate();
  const elements = [
    {
      name: "Ticket",
      value: ticket,
    },
    {
      name: "Department",
      value: department,
    },
    {
      name: "Employee",
      value: employee,
    },
    {
      name: "Profile",
      value: profile,
    },
  ];

  const user = JSON.parse(localStorage.getItem("user"));

  const [active, setActive] = useState("All Ticket");
  return (
    <>
     
      <Nav tag={elements} setActive={setActive} />
       {user && user.role === "ROLE_ADMIN"? (<Outlet></Outlet>): navigate("/")}
       </>
  );
}

export default AdminDashboard;
