import axios from 'axios';
import React, { useState } from 'react'
// import "./Login.css"


function Login() {

  
  const [data , setData] = useState({
    userName:"",
    password:""
  });

  const handleChange=(e)=>{
    setData({...data , [e.target.name]: e.target.value});
    // console.log(data);
  }

  const handleSubmit=(e)=>{
    e.preventDefault();
    axios.post("http://localhost:8080/login",data)
    .then(response=>{
      alert(response.data)
    })
  }

  return (
    <div className='container'>
      <div class="form-div">
      <form >
      <h1>Login!</h1>
      
        <label for="uname"><b>Email</b></label>
        <input type="text" placeholder="Enter Username" name="userName" value={data.userName} onChange={handleChange} required/>

        <label for="psw"><b>Password</b></label>
        <input type="password" placeholder="Enter Password" name="password" value={data.password} onChange={handleChange} required/>

        <button type="submit" onClick={handleSubmit}>Login</button>
      
    </form>
  </div>
  
  </div>

  
  )
}

export default Login
