import React, { useState } from "react";
import "../css/Nav.css";
import { Link, useNavigate } from "react-router-dom";
import AddDepartment from "./AddDepartment";
import ResetPassword from "./ResetPassword";

function Navbar(props) {
  const navigate = useNavigate();
  const [dep, setDep] = useState(false);
  const [resetPass, setResetPass] = useState(false);
  return (
    <>
      {dep ? <AddDepartment set={setDep} /> : null}
      {resetPass ? <ResetPassword set={setResetPass} /> : null}

      <nav>
        <span class="logo">Grievance Management</span>
        <ul>
          {props.tag.map((item) => {
            console.log(item);
            return 
            // item.name === "Create Department" ? (
            //   <li>
            //     <a class="active" onClick={() => setDep(true)}>
            //       Add Department
            //     </a>
            //   </li>
            // ) : item.name === "Add User" ? (
            //   <li>
            //     <a
            //       class="active"
            //       onClick={() => {
            //         setResetPass(true);
            //       }}
            //     >
            //       Add User
            //     </a>
            //   </li>
            // ) : (
              <li >
                <div className="drop-down">
                  <a className="active nav-link">{item.name}</a>
                  <div className="drop-down-content">
                    {item.value.map(value=>{return<a onClick={()=>{props.setActive(value)}}>{value}</a>})}
                  </div>
                </div>
              </li>
            // );
          })}
        </ul>
      </nav>
    </>
  );
}

export default Navbar;
