import axios from 'axios';
import React, { useState } from 'react'
// import "./Login.css"

function Login() {

  const [data , setData] = useState({
    "userName":"",
    "password":""
  });

  const handleChange=(e)=>{
    setData({...data , [e.target.name]: e.target.value});
    // console.log(data);
  }

  const handleSubmit=(e)=>{
    e.preventDefault();
    axios.post("http://localhost:8080/login",data)
    .then(response=>{
      alert(response.data)
    })
  }

  return (
    <div className='container'>
      <form>
        <h2>Registration</h2>
      <div className="form-group">
        <label for="exampleInputEmail1">Email address</label>
        <input type="email" className="form-control" id="userName" aria-describedby="emailHelp" placeholder="Enter email" name='userName' onChange={handleChange} value={data.userName}/>
       
      </div>
      <div className="form-group">
        <label for="exampleInputPassword1">Password</label>
        <input type="password" className="form-control" id="password" placeholder="Password" name='password' value={data.password} onChange={handleChange}/>
      </div>
      
      <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit</button>
    </form>
  </div>

  
  )
}

export default Login
