import React, { useState } from "react";
import "../css/Navbar.css";
import { Link, useNavigate } from "react-router-dom";
import AddDepartment from "../Pages/AddDepartment";
import ResetPassword from "../Pages/ResetPassword";

function Navbar(props) {
  const navigate = useNavigate();
  const [dep, setDep] = useState(false);
  const [resetPass , setResetPass] = useState(false);
  const element = props.tag;
  console.log(element)
  return (
    <>
      {dep ? <AddDepartment set={setDep} /> : null}
      {resetPass ? <ResetPassword set={setResetPass}/> : null}

      <nav>
      <span class="logo">Grievance Management</span>
      <ul>
        {element.map(item=>{
          return( 
            <div class="dropdown-menu">
            <button class="menu-btn">{item.name}</button>
            <div class="menu-content">
            {   
                item.value.map((value)=>(
                    <a class="links-hidden" onClick={()=>props.setActive(value)}>{value}</a>
                ))
}
</div>
</div>)
          
        })}
      </ul>
      </nav>
    </>
  );
}

export default Navbar;
