import axios from "axios";

const headers=()=>{
    const header = {
        email : JSON.parse(localStorage.getItem("data")).email,
        password : localStorage.getItem("password")
    }
    return header;
}

const getMapping=(props)=>{
    return axios.get(`${process.env.REACT_APP_ENV_VARIABE}`+props,{headers:headers()});
}


export const getAllDepartment=()=>{
    return getMapping(`/department/getall`);
    // return axios.get(`${process.env.REACT_APP_ENV_VARIABE}/department/getall`,{headers:headers()});
}

export const addDepartment=(department)=>{
    return axios.post("http://localhost:8080/department/add",department ,{
        // withCredentials: true,
        headers:{
            // 'Content-Type': 'application/json',
            // 'Access-Control-Allow-Origin': '*',
            // 'Access-Control-Allow-Headers': '*',
            // 'Access-Control-Allow-Credentials': true,
            email:'adarsh123@gmail.com',
            password:'adarsh'
        }
        
    });
}

export const deleteDepartmentById=(id)=>{
    console.log(id);
    return axios.delete(`http://localhost:8080/department/delete/${id}`);
}
