import axios from "axios";
import { headers } from "./Headers";
import { getMapping } from "./AllUrls";

export const getAllDepartment=()=>{
    return getMapping(`/department/getall`,{headers:headers()})
}
export const addDepartment=(department)=>{
    return axios.post(`${process.env.REACT_APP_ENV_VARIABE}`+`/department/add`,department ,{headers:headers()});
}

export const deleteDepartmentById=(id)=>{
    return axios.delete(`${process.env.REACT_APP_ENV_VARIABE}`+`/department/delete/${id}`,{headers:headers()});
}
