import React, { useEffect, useState } from 'react'
import "../registration/UserRegistration.css"
import { getAllDepartment } from '../../api/Department_API';

function AddTicket() {

    const [department , setDepartment] = useState([]);

    const getDepartment=()=>{
    getAllDepartment().then((resp)=>{
      setDepartment(resp.data);
      console.log(resp.data);
    }).catch((error)=>{
      console.log(error);
    })
    }

    useEffect(()=>{
    getDepartment();
    } , [])
  return (
    <div className='registration'>
      <div className='content'>
      <div className='header'>
        <h1>Add Ticket</h1>
      </div>
      
        <form>
        <div className='user-details'>
          <div className='inner-div'>
            <label>Username</label>
            <input type='text' placeholder='Enter your name'  />
          </div>
          <div className='inner-div'>
            <label>Email</label>
            <input type='email' placeholder='Enter your email' value='' onChange='' />
          </div>
          <div className='inner-div'>
            <label>Password</label>
            <input type='password' placeholder='Enter your password' value='' onChange='' />
          </div>
          <div className='inner-div'>
            <label for="cars">Role:</label>

            <select name="Role" id="cars">
              <option value="volvo">ADMIN</option>
              <option value="saab">USER</option>
            </select>
          </div>

          <div className='inner-div'>
            <label for="cars">Department:</label>

            <select name="Role" id="cars">
              {department.map(dep=>{
                  return <option value={dep.depName}>{dep.depName}</option>
              })}
              
              {/* <option value="saab">fired</option> */}
            </select>
          </div>

          <div className='input-box'>
                <input type='submit' value='Submit'  />
          </div>

          </div>
        </form>
        </div>
    </div>
  )
}

export default AddTicket
