import "./Modal.css";

export default function Modal(props) {


  return (
    <>
        <div className="modal">
          <div className="modal-content">
            <h3>Add Deaprtment</h3>
            
            <button className="close-modal" onClick={()=>{props.setModal(false)}}>
              X
            </button>
          </div>
        </div>
      
    </>
  );
}
