import React, { useEffect, useState } from "react";
import "../components/Department.css";
import axios from "axios";
import TableHeader from "../components/Table/TableHeader";
import Content from "../components/Table/Content";

function AllTickets() {
  const [ticket, setTicket] = useState([]);
  const [selectedStatus , setSelectedStatus] = useState("all");
  const [offset , setOffset] = useState(0);

  useEffect(() => {
    axios
      .get(`http://localhost:8080/ticket/getall/?empid=${19}&ticket=${"all"}&filter=${setSelectedStatus}&offset=${offset}`,{headers:{
        email:"adarsh123@gmail.com",
        password:"adarsh"
      }})
      .then((resp) => {
        setTicket(resp.data);
      });
  },[]);

  
  const header = [
    "Ticket Id",
    "Ticket Type",
    "Status",
    "Ticket Title",
    "Last Update",
    "Actions",
  ];

  const handleSelectChange=(e)=>{
    setSelectedStatus(e.target.value);

  }

  return (
    <div className="department-table">
      <div>
        <h1>All Tickets</h1>
      </div>
      <div>
        <label>Status:</label>

        <select name="status" defaultValue="ALL" value={selectedStatus} onChange={handleSelectChange}>
          <option value="all">ALL TICKETS</option>
          <option value="open">OPEN</option>
          <option value="progress">BEING_ADDRESSED</option>
          <option value="resolved">RESOLVED</option>
        </select>
      </div>

      <table>
        <TableHeader header={header} />

        {ticket.map((d) => {
          return (
            <tr>
              <Content data={d.ticketId} />
              <Content data={d.ticketType} />
              <Content data={d.status} />
              <Content data={d.ticketName} />
              <Content data={d.lastUpdateDate} />
              <td>
                <button>View</button>
              </td>
            </tr>
          );
        })}
      </table>
      <div>
        <button disabled={offset===0} onClick={()=>{setOffset(offset-1)}}>previous</button>
        <button disabled={ticket.length === 0} onClick={()=>{setOffset(offset+1)}}>Next</button>
      </div>
    </div>
  );
}

export default AllTickets;
